nta.declaration:
// Place global declarations here.
int x_direction(int direction){
    if (direction == 1){
        return 1;
    }
    if (direction == 3){
        return -1;
    }
    return 0;
}

int y_direction(int direction){
    if (direction == 0){
        return 1;
    }
    if (direction == 2){
        return -1;
    }
    return 0;
}

int reverse(int direction){
    if (direction != 0){
        return direction * -1;
    }
    return 0;
}

bool inside(int x, int y, int r){
    return x*x + y*y <= r*r;
}

nta.system:
// Place template instantiations here.
P1 = Robot(false, false, false, 0, 0, 0, 0, 4);
// List one or more processes to be composed into a system.
system P1;
