nta.declaration:
// Place global declarations here.

// Number of robots - N, Beta parameter - BETA, Signal radius - R, Grid boundary - G => Grid : 2G x 2G
const int N = 3;
const int R = 1;
const int STEP = 1;
const int BETA = 1;
const int G = 3;
const int T_MAX = 5;
clock C;


// Robot coordinates
int x[N];
int y[N];

// Robot directions
int x_dir[N];
int y_dir[N];

// List of current and last neighbours lists for each robot
// Convention: 
//    - id of the robot will point to its neighbours list
//    - 1 at position id indicates connection with robot of a given id, 0 otherwise
int neighbours[N][N];
int last_neighbours[N][N];
int lost_neighbours[N][N];
int n_shared_neighbours[N];
int distances[N][N];

// Number of connections and last connections for each robot
int k[N];
int last_k[N];

// Map one of four directionos into x_dir and y_dir
void mapDirection(int id, int dir){
    if (dir == 0){
		// up
        x_dir[id] = 0;
        y_dir[id] = 1;
    }
    if (dir == 1){
		// right
        x_dir[id] = 1;
        y_dir[id] = 0;
    }
    if (dir == 2){
		// down
        x_dir[id] = 0;
        y_dir[id] = -1;
    }
    if (dir == 3){
		// left
        x_dir[id] = -1;
        y_dir[id] = 0;
    }
}

// Reverse current direction of the robot
void reverseDirection(int id){
	x_dir[id] *= -1;
	y_dir[id] *= -1;
}

// Move robot forward in its current direction
void moveForward(int id){
	x[id] += x_dir[id] * STEP;
	y[id] += y_dir[id] * STEP;
}

// Checks if robot is on the boundary of the grid
bool gridBoundary(int id){
    return abs(x[id]) == G || abs(y[id]) == G;
}

// Updates k and last_k
void updateConnections(int id){
   int i;
   int n;
   last_k[id] = k[id];
   for (i = 0; i < N; i++){
       if (neighbours[id][i] == 1){
           n++;
       }
   }
   k[id] = n;
}

// Decide if robot should make a random turn
bool turnRandom(int id){
    return k[id] > last_k[id];
}

// Check if there are neighbours that were lost for a given robot
bool noLostNeighbours(int id){
    int i;
    for (i = 0; i < N; i++){
        if(last_neighbours[id][i] != 0){
            return false;
        }
    }
    return true;
}

// Decide if robot should turn around
bool turn180(int id){
    if (noLostNeighbours(id)){
        return false;
    }
    return n_shared_neighbours[id] <= BETA;
}

// Calculates Euclidean distance between robots
double distance(int x1, int y1, int x2, int y2){
    return sqrt(pow(abs(x1 - x2), 2) + pow(abs(y1 - y2), 2));
}

// Checks if distance between robots is within a signal threshold
bool connected(double distance, double threshold){
    return distance <= threshold;
}

// Update distances
void updateDistances(){
    int i;
    int j;
    for (i = 0; i < N; i++){
        for (j = 0; j < N; j++){
            distances[i][j] = fint(distance(x[i], y[i], x[j], y[j]));
        }
    }
}

// Updates lost_neighbours
void lostNeighbours(int id){
    int i;
    for (i = 0; i < N; i++){
        if (last_neighbours[id][i] == 1 && neighbours[id][i] == 0){
            lost_neighbours[id][i] = 1;
        } else {
            lost_neighbours[id][i] = 0;
        }
    }
}


// Returns the number of neighbours shared between two robots
int sharedNeighbours(int id1, int id2){
    int shared;
    int i;
    for (i = 0; i < N; i++){
        if(neighbours[id1][i] == neighbours[id2][i] && neighbours[id1][i] == 1){
            shared++;
        }
    }
    return shared;
}

// Updates n_shared_neighbours
void nSharedNeighbours(int id){
    int i;
    int n_shared;
    for (i = 0; i < N; i++){
        if (last_neighbours[id][i] == 1 && neighbours[id][i] == 0){
            n_shared += sharedNeighbours(id, i);
        }
    }
    n_shared_neighbours[id] = n_shared;
}

void updateNeighbours(int id){
    int i;
    last_neighbours[id] = neighbours[id];
    for (i = 0; i < N; i++){
            if (connected(distance(x[id], y[id], x[i], y[i]), R) && id != i){
                neighbours[id][i] = 1;
            } else {
                neighbours[id][i] = 0;
            }
    }
    lostNeighbours(id);
    nSharedNeighbours(id);
}

nta.system:
// Place template instantiations here.
P0 = Robot(0);
P1 = Robot(1);
P2 = Robot(2);
//P3 = Robot(3);
//P4 = Robot(4);


// List one or more processes to be composed into a system.
system P0, P1, P2;
//,P3, P4;
